import { useState, useEffect } from "react";
import * as React from "react";
import styles from "./ValvolineHrProducts.module.scss";

import { IValvolineHrProductsProps } from "./IValvolineHrProductsProps";
import { IHumanResourceRoleCategoryResourceFilter } from "../../../data/IHumanResourceRoleCategoryResourceFilter";
import { SPHttpClient } from "@microsoft/sp-http";
import { IFilePickerResult } from "@pnp/spfx-property-controls";

type IProductAsset = {
	Title: string;
	Id: string;
	Description: string;
	Description2: string;
	Link: string;
	LinkText: string;
	RolesId: string[];
	DataUri: string;
	Categories: string[];
	Roles: string[];
	rolesID: number[];
	ResourceType: string[];
	SortOrder: string;
	IconReference: string;
	IconImage: string;
	ShowInResults: boolean;
};

const ValvolineHrProducts = (Props: IValvolineHrProductsProps) => {
	
	//change the styling of the hiddenLink.
	//if(document.getElementById("hiddenLink") != null)document.getElementById("hiddenLink").setAttribute('style',Props.bodyAreaHiddenLinkCSS);

	if(document.getElementById("valvolineHrProducts_Section") != null) {
		//set the background image
		document.getElementById("valvolineHrProducts_Section").style.backgroundImage = 'url('+Props.filePickerResult.fileAbsoluteUrl+')';
		//cover the background image to stretch and scale over section
		document.getElementById("valvolineHrProducts_Section").style.backgroundSize = 'cover';
		//console.log(Props.filePickerResult.fileAbsoluteUrl);
	}

	const [listItems, setListItems] = useState<IProductAsset[]>([]);

	// Create the SVGs for users dynamically
	const generateSVGs = (rolesID: number[]): JSX.Element[] => {
		//console.log(roles);
		if(Props.icon1PickerResult.fileAbsoluteUrl != null && Props.icon2PickerResult.fileAbsoluteUrl != null && 
			Props.icon3PickerResult.fileAbsoluteUrl != null) {
				const SVGList: JSX.Element[] = [];
				// List of roles with their respective color/initials
				console.log(rolesID);
				const roleStyleInfo: { [key: number]: { color: string; initials: string; imgLink: IFilePickerResult; } } = {
					"1": { color: "#e1261c", initials: "D+",  imgLink: Props.icon1PickerResult},
					"2": { color: "#013866", initials: "M", imgLink: Props.icon2PickerResult},
					"3": { color: "#009cd9", initials: "IC", imgLink: Props.icon3PickerResult},
				};
				for (const roleInfo of rolesID) {
					SVGList.push(
						<img className={styles.usersText} style={inlineStylesToObject(Props.userIconCSS)} src={roleStyleInfo[roleInfo].imgLink.fileAbsoluteUrl}></img>
					);
				}
				return SVGList;
		}
	};

	// Call Sharepoint using SPHttpClient to recieve list Items
	const getListItems = async () => {
		//flip these when testing local
		//const url: string = `${Props.spfxContext.pageContext.web.absoluteUrl}/sites/ValvolineDevelopmentGatewayPortal/_api/web/lists/GetByTitle('Product Assets Upload')/items?$select=ShowInResults,Title,Description,Description2,LinkText,IconReference,IconImage,Link,Roles/Title,Roles/Id,Categories/Title,Categories/Id,ResourceType/Title,SortOrder&$expand=Roles,Categories,ResourceType`;
		const url: string = `${Props.spfxContext.pageContext.web.absoluteUrl}/_api/web/lists/GetByTitle('Product Assets Upload')/items?$select=ShowInResults,Title,Description,Description2,LinkText,IconReference,IconImage,Link,Roles/Title,Roles/Id,Categories/Title,Categories/Id,ResourceType/Title,SortOrder&$expand=Roles,Categories,ResourceType&$top=1000`;
		
		try {
			const response = await Props.spfxContext.spHttpClient.get(
				url,
				SPHttpClient.configurations.v1
			);
			if (response.ok) {
				const json = await response.json();
				setListItems(json.value);
			} else {
				console.log(`${response.status}: ${response.statusText}`);
			}
		} catch (error: any) {
			console.error(error);
		}
	};

	enum ImageLookupEnum {
		/*
		Coaching_One_On_One = "",
		SelfDirected = "data:image/png;base64,",
		DevGuide = "data:image/png;base64,",
		ManagerGuide = "data:image/png;base64,",
		OnlineLearn = "data:image/png;base64,",
		Assessment = "data:image/png;base64,",
		ConvPlanner = "data:image/png;base64,",		
		*/
		assessment = "data:image/png;base64,",
		coaching_one_on_one = "data:image/png;base64,",
		conversation_planner = "data:image/png;base64,",
		development_guide = "data:image/png;base64,",
		discussion_planner = "data:image/png;base64,",
		feedback_survey = "data:image/png;base64,",
		managers_guide = "data:image/png;base64,",
		online_learning = "data:image/png;base64,",
		quick_reference_guide = "data:image/png;base64,",
		self_assessment = "data:image/png;base64,",
		self_directed_learning = "data:image/png;base64,",
	}

	const showListItems = listItems.filter((li) => {
		return li.ShowInResults;
	});
	const products = showListItems.map((li) => {
		const roleArray = li.Roles.map((r: any) => r.Title);
		const roleIDArray = li.Roles.map((r: any) => r.Id);
		const categoryArray = li.Categories.map((c: any) => c.Title);
		const resourceArray = li.ResourceType.map((r: any) => r.Title);
		if (li.IconReference == null && resourceArray.length > 0) {
			if (resourceArray[0].indexOf("1:1") >= 0) {
				li.IconReference = "coaching_one_on_one";
			} else {
				li.IconReference = resourceArray[0]
					.toLowerCase()
					.replaceAll(" ", "_")
					.replaceAll("-", "_");
			}
		}
		let linkDestination = "";
		// Check Link Pathing
		// When starting with a slash, it points to a site page,
		if (li.Link[0] === "/" || li.Link[0] === "\\") {
			linkDestination = `${Props.spfxContext.pageContext.web.absoluteUrl}/SitePages${li.Link}`;
			// When starting with http/https/www it is an absolute path.
		} else if (
			li.Link.substring(0, 8) === "https://" ||
			li.Link.substring(0, 7) === "http://" ||
			li.Link.substring(0, 4) === "www."
		) {
			linkDestination = li.Link;
			// Default is a file within a specific Sharepoint folder
		} else {
			linkDestination = `${Props.spfxContext.pageContext.web.absoluteUrl}/Shared%20Documents/HRAssets_Files/${li.Link}`;
		}

		return {
			title: li.Title,
			desc:
				li.Description && li.Description.length >= 120
					? li.Description.slice(0, 117) + "..."
					: li.Description,
			desc2:
				li.Description2 && li.Description2.length >= 120
					? li.Description2.slice(0, 117) + "..."
					: li.Description2,
			link: linkDestination,

			linkText:
				li.LinkText && li.LinkText.length >= 60
					? li.LinkText.slice(0, 57) + "..."
					: li.LinkText,
			roles: roleArray,
			rolesID: roleIDArray,
			categories: categoryArray,
			resourceTypes: resourceArray,
			sortOrder: li.SortOrder,
			icon: ImageLookupEnum[li.IconReference as keyof typeof ImageLookupEnum],
			iconImage: li.IconImage,
		};
	});

	const categoryData: IHumanResourceRoleCategoryResourceFilter | undefined =
		Props.humanResourceFilter.tryGetValue();

	const filteredProducts = products.filter((p) => {
		let show_because_role = false;
		let show_because_category = false;
		let show_because_resource_type = false;
		if (typeof categoryData !== "undefined") {
			if (
				!categoryData.role_filter ||
				categoryData.role_filter === "--all--" ||
				categoryData.role_filter.trim() === "" ||
				p.roles.indexOf(categoryData.role_filter) > -1
			) {
				show_because_role = true;
			}
			if (
				!categoryData.category_filter ||
				categoryData.category_filter === "--all--" ||
				categoryData.category_filter.trim() === "" ||
				p.categories.indexOf(categoryData.category_filter) > -1
			) {
				show_because_category = true;
			}
			if (
				!categoryData.resource_type_filter ||
				categoryData.resource_type_filter === "--all--" ||
				categoryData.resource_type_filter.trim() === "" ||
				p.resourceTypes.indexOf(categoryData.resource_type_filter) > -1
			) {
				show_because_resource_type = true;
			}
		}
		return show_because_role && show_because_category && show_because_resource_type;
	});

	// Sort by ascending order, unless sortOrder is not there, or set to default (-1)
	filteredProducts.sort((a, b) => {
		if (!a.sortOrder || parseFloat(a.sortOrder) === -1) return 1;
		if (!b.sortOrder || parseFloat(b.sortOrder) === -1) return -1;
		return parseFloat(a.sortOrder) - parseFloat(b.sortOrder);
	});

	let numberOfCards = null;
	if (Props.numberOfCardsToDisplay && Props.numberOfCardsToDisplay.trim() != "") {
		try {
			numberOfCards = parseInt(Props.numberOfCardsToDisplay);
		} catch (ex) {
			console.log(ex);
		}
	}

	const displayProducts =
		numberOfCards !== null ? filteredProducts.slice(0, numberOfCards) : filteredProducts;
	

	//converts string of CSS into a useable style
	function inlineStylesToObject(styles: string) : Record<string, string> {

		const regex = /([\w-]+)\s*:\s*((?:(?:"[^"]+")|(?:'[^']+')|[^;])*);?/g;
		  
		const obj : Record<string, string> = {};
		  
		let match;
		while (match = regex.exec(styles)) {
			obj[match[1]] = match[2].trim();
		}
			return obj;
	}

	const cardRow = displayProducts.map((p, index: number) => {
		return (
			<div key={index} className={`${styles.card}`} style={inlineStylesToObject(Props.cardCSS)}>
				<div style={inlineStylesToObject(Props.topBarCSS)}>
					<div className={styles.titleArea}>
						{p.title ? <h1 className={styles.mainTitle}>{p.title}</h1> : "Loading..."}
					</div>
				</div>
				<div className={styles.iconArea} style={inlineStylesToObject(Props.iconAreaCSS)}>
				    <img src={JSON.parse(p.iconImage).serverUrl+JSON.parse(p.iconImage).serverRelativeUrl} style={inlineStylesToObject(Props.iconCSS)}></img>
					</div>
				<div className="resourceTypeArea" style={inlineStylesToObject(Props.resourceTypeAreaCSS)}>
					{p.resourceTypes ? <p className={styles.resourceType}>{p.resourceTypes}</p> : "Loading..."}
					</div>	
				<div className="bodyArea" style={inlineStylesToObject(Props.bodyAreaCSS)}>
					{p.linkText ? (
						<>
							<h2 className={styles.title} style={inlineStylesToObject(Props.bodyAreaTitleCSS)}>
								<a
									href={p.link}
									rel="noreferrer"
									target="_blank"
									data-interception="off"
									//className={styles.hiddenLink}
									//id="hiddenLink"
									style={inlineStylesToObject(Props.bodyAreaHiddenLinkCSS)}
								>
									{p.linkText}
								</a>
							</h2>
							<p style={inlineStylesToObject(Props.bodyAreaTextCSS)}>{p.desc}</p>
							<p style={inlineStylesToObject(Props.bodyAreaText2CSS)}>{p.desc2}</p>
						</>
					) : (
						"Loading..."
					)}
				</div>
				<div className={styles.userArea} style={inlineStylesToObject(Props.userAreaCSS)}>{generateSVGs(p.rolesID)}</div>
			</div>
		);
	});

	// On Mounting, get List Items
	useEffect(() => {
		getListItems();
	}, []);

	return (
		<section
			className={`${styles.valvolineHrProducts} ${
				Props.hasTeamsContext ? styles.teams : ""
			}`} id="valvolineHrProducts_Section"
	    >
			<div className="resultRow" style={inlineStylesToObject(Props.resultRowCSS)}>
				{Props.toggleResults ? (
									<p>
									{displayProducts.length < filteredProducts.length &&
										`Results: ` + displayProducts.length + ` of ` + filteredProducts.length}
									{displayProducts.length == filteredProducts.length &&
										`Results: ` + filteredProducts.length}
								</p>
				) : (
					<></>
				)}
				{Props.toggleViewAll ? (
					<a
						rel="noreferrer"
						target="_blank"
						href={`${Props.spfxContext.pageContext.web.absoluteUrl}/SitePages/Role-Results.aspx?role=${encodeURIComponent(categoryData.role_filter)}&category=${categoryData.category_filter}&resource=${categoryData.resource_type_filter}`}
					>
						View All
					</a>
				) : (
					<></>
				)}
			</div>
			<div className={`${styles.cardRow}`}>
				{Props.needsConfiguration &&
					`Needs Connected to Source Role, Category, Resource Type Filter`}
				{!Props.needsConfiguration && cardRow}
			</div>
			{Props.toggleViewAllBottom ? (
					<a
						rel="noreferrer"
						target="_blank"
						href={`${Props.spfxContext.pageContext.web.absoluteUrl}/SitePages/Role-Results.aspx?role=${encodeURIComponent(categoryData.role_filter)}&category=${categoryData.category_filter}&resource=${categoryData.resource_type_filter}`}
						style={inlineStylesToObject(Props.viewAllBottomCSS)}
					>
						{Props.viewAllButtonLabel}
					</a>
				) : (
					<></>
				)}
		</section>
	);
};

export default ValvolineHrProducts;
